import React from 'react';
import { motion } from 'framer-motion';
import { FaBuilding, FaFacebook, FaTwitter, FaInstagram, FaLinkedin, FaYoutube, FaArrowUp } from 'react-icons/fa';

const Footer = () => {
  const scrollToTop = () => {
    window.scrollTo({ top: 0, behavior: 'smooth' });
  };

  const footerLinks = {
    company: [
      { name: 'Hakkımızda', href: '#about' },
      { name: 'Ekibimiz', href: '#' },
      { name: 'Kariyer', href: '#' },
      { name: 'Haberler', href: '#' }
    ],
    services: [
      { name: 'Ticari İnşaat', href: '#services' },
      { name: 'Konut Projeleri', href: '#services' },
      { name: 'Endüstriyel İnşaat', href: '#services' },
      { name: 'Mimari Tasarım', href: '#services' }
    ],
    projects: [
      { name: 'Portföy', href: '#projects' },
      { name: 'Vaka Çalışmaları', href: '#' },
      { name: 'Ödüller', href: '#' },
      { name: 'Müşteri Yorumları', href: '#' }
    ],
    support: [
      { name: 'İletişim', href: '#contact' },
      { name: 'Teklif Al', href: '#contact' },
      { name: 'SSS', href: '#' },
      { name: 'Destek', href: '#' }
    ]
  };

  const socialLinks = [
    { icon: <FaFacebook />, href: '#', label: 'Facebook' },
    { icon: <FaTwitter />, href: '#', label: 'Twitter' },
    { icon: <FaInstagram />, href: '#', label: 'Instagram' },
    { icon: <FaLinkedin />, href: '#', label: 'LinkedIn' },
    { icon: <FaYoutube />, href: '#', label: 'YouTube' }
  ];

  return (
    <footer className="bg-gray-900 text-white">
      {/* Main Footer Content */}
      <div className="container-custom py-16">
        <div className="grid lg:grid-cols-5 gap-8">
          {/* Company Info */}
          <div className="lg:col-span-2">
            <div className="flex items-center mb-6">
              <FaBuilding className="text-3xl text-blue-400 mr-3" />
              <span className="text-2xl font-bold">Sitee</span>
            </div>
            <p className="text-gray-300 mb-6 leading-relaxed">
              İnovasyon, kalite ve mükemmellik ile yarının vizyonunu inşa ediyoruz. 
              15 yılı aşkın süredir inşaat ve mimarlık alanında öncü konumdayız.
            </p>

            {/* Social Links */}
            <div className="flex space-x-4">
              {socialLinks.map((social, index) => (
                <motion.button
                  key={index}
                  whileHover={{ scale: 1.1, y: -2 }}
                  whileTap={{ scale: 0.9 }}
                  className="w-10 h-10 bg-gray-800 hover:bg-blue-600 rounded-lg flex items-center justify-center transition-all duration-300"
                  aria-label={social.label}
                >
                  {social.icon}
                </motion.button>
              ))}
            </div>
          </div>

          {/* Footer Links */}
          <div>
            <h4 className="text-lg font-semibold mb-6">Şirket</h4>
            <ul className="space-y-3">
              {footerLinks.company.map((link, index) => (
                <li key={index}>
                  <a
                    href={link.href}
                    className="text-gray-300 hover:text-blue-400 transition-colors duration-300"
                  >
                    {link.name}
                  </a>
                </li>
              ))}
            </ul>
          </div>

          <div>
            <h4 className="text-lg font-semibold mb-6">Hizmetler</h4>
            <ul className="space-y-3">
              {footerLinks.services.map((link, index) => (
                <li key={index}>
                  <a
                    href={link.href}
                    className="text-gray-300 hover:text-blue-400 transition-colors duration-300"
                  >
                    {link.name}
                  </a>
                </li>
              ))}
            </ul>
          </div>

          <div>
            <h4 className="text-lg font-semibold mb-6">Projeler</h4>
            <ul className="space-y-3">
              {footerLinks.projects.map((link, index) => (
                <li key={index}>
                  <a
                    href={link.href}
                    className="text-gray-300 hover:text-blue-400 transition-colors duration-300"
                  >
                    {link.name}
                  </a>
                </li>
              ))}
            </ul>
          </div>
        </div>

        {/* Newsletter Signup */}
        <motion.div
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
          className="mt-16 bg-gray-800 rounded-2xl p-8"
        >
          <div className="grid lg:grid-cols-2 gap-8 items-center">
            <div>
              <h3 className="text-2xl font-bold mb-4">
                Güncel Kalın
              </h3>
              <p className="text-gray-300">
                En son inşaat içgörüleri, proje güncellemeleri ve 
                sektör trendleri için bültenimize abone olun.
              </p>
            </div>
            <div className="flex flex-col sm:flex-row gap-4">
              <input
                type="email"
                placeholder="E-posta adresinizi girin"
                className="flex-1 px-4 py-3 bg-gray-700 border border-gray-600 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent text-white placeholder-gray-400"
              />
              <motion.button
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
                className="bg-blue-600 hover:bg-blue-700 text-white font-semibold py-3 px-6 rounded-lg transition-all duration-300"
              >
                Abone Ol
              </motion.button>
            </div>
          </div>
        </motion.div>
      </div>

      {/* Bottom Footer */}
      <div className="border-t border-gray-800">
        <div className="container-custom py-8">
          <div className="flex flex-col md:flex-row justify-between items-center">
            <div className="text-gray-400 text-sm mb-4 md:mb-0">
              © 2024 Sitee İnşaat. Tüm hakları saklıdır.
            </div>

            <div className="flex items-center space-x-6 text-sm text-gray-400">
              <button className="hover:text-blue-400 transition-colors duration-300">
                Gizlilik Politikası
              </button>
              <button className="hover:text-blue-400 transition-colors duration-300">
                Kullanım Şartları
              </button>
              <button className="hover:text-blue-400 transition-colors duration-300">
                Çerez Politikası
              </button>
            </div>
          </div>
        </div>
      </div>

      {/* Scroll to Top Button */}
      <motion.button
        onClick={scrollToTop}
        whileHover={{ scale: 1.1 }}
        whileTap={{ scale: 0.9 }}
        className="fixed bottom-8 right-8 w-12 h-12 bg-blue-600 hover:bg-blue-700 text-white rounded-full shadow-lg flex items-center justify-center transition-all duration-300 z-50"
        aria-label="Yukarı çık"
      >
        <FaArrowUp />
      </motion.button>
    </footer>
  );
};

export default Footer;
