import React from 'react';
import { motion } from 'framer-motion';
import { useInView } from 'react-intersection-observer';
import { FaAward, FaUsers, FaHandshake, FaLightbulb } from 'react-icons/fa';

const About = () => {
  const [ref, inView] = useInView({
    threshold: 0.1,
    triggerOnce: true,
  });

  const values = [
    {
      icon: <FaAward className="text-3xl" />,
      title: "Kalite",
      description: "Her projede en yüksek kalite standartlarını hedefliyoruz."
    },
    {
      icon: <FaUsers className="text-3xl" />,
      title: "Güven",
      description: "Müşterilerimizle yakın çalışarak vizyonlarını hayata geçiriyoruz."
    },
    {
      icon: <FaHandshake className="text-3xl" />,
      title: "Dürüstlük",
      description: "Şeffaf iletişim ve dürüst iş anlayışı ile güven inşa ediyoruz."
    },
    {
      icon: <FaLightbulb className="text-3xl" />,
      title: "Yenilik",
      description: "En son teknoloji ve sürdürülebilir uygulamaları benimsiyoruz."
    }
  ];

  return (
    <section id="about" className="section-padding bg-gray-50">
      <div className="container-custom">
        <motion.div
          ref={ref}
          initial={{ opacity: 0, y: 50 }}
          animate={inView ? { opacity: 1, y: 0 } : {}}
          transition={{ duration: 0.8 }}
          className="text-center mb-16"
        >
          <h2 className="text-4xl md:text-5xl font-bold text-gray-900 mb-6">
            Hakkımızda
          </h2>
          <p className="text-xl text-gray-600 max-w-3xl mx-auto">
            15 yılı aşkın deneyimimizle inşaat sektöründe güvenilir bir marka haline geldik.
          </p>
        </motion.div>

        <div className="grid lg:grid-cols-2 gap-12 items-center mb-16">
          <motion.div
            initial={{ opacity: 0, x: -50 }}
            animate={inView ? { opacity: 1, x: 0 } : {}}
            transition={{ duration: 0.8, delay: 0.2 }}
            className="relative"
          >
            <div className="w-full h-96 bg-gradient-to-br from-blue-600 to-blue-800 rounded-2xl shadow-2xl flex items-center justify-center">
              <div className="text-center text-white">
                <div className="text-6xl mb-4">🏢</div>
                <p className="text-xl font-semibold">Modern Ofis Kompleksi</p>
                <p className="text-sm opacity-80">2023 Tamamlandı</p>
              </div>
            </div>
          </motion.div>

          <motion.div
            initial={{ opacity: 0, x: 50 }}
            animate={inView ? { opacity: 1, x: 0 } : {}}
            transition={{ duration: 0.8, delay: 0.4 }}
            className="space-y-6"
          >
            <h3 className="text-3xl font-bold text-gray-900 mb-6">
              Hayallerinizi Gerçeğe Dönüştürüyoruz
            </h3>

            <p className="text-lg text-gray-600 leading-relaxed">
              2009 yılında kurulan Sitee İnşaat, küçük bir yerel müteahhitlik firmasından 
              bölgenin en güvenilir inşaat ve mimarlık firmalarından biri haline geldi. 
              Yolculuğumuz inovasyon, kalite ve müşteri başarısına olan sarsılmaz 
              bağlılığımızla işaretlendi.
            </p>

            <p className="text-lg text-gray-600 leading-relaxed">
              Ticari, konut ve endüstriyel inşaat konularında uzmanlaşmış olup, 
              ilk tasarımdan nihai tamamlanmaya kadar kapsamlı hizmetler sunuyoruz. 
              Deneyimli profesyonellerimizden oluşan ekibimiz mimarlık, mühendislik 
              ve proje yönetimi konularında uzmanlık bir araya getiriyor.
            </p>

            <div className="grid grid-cols-2 gap-6 pt-6">
              <div className="text-center">
                <div className="text-3xl font-bold text-blue-600 mb-2">100+</div>
                <div className="text-gray-600">Tamamlanan Proje</div>
              </div>
              <div className="text-center">
                <div className="text-3xl font-bold text-blue-600 mb-2">50+</div>
                <div className="text-gray-600">Mutlu Müşteri</div>
              </div>
            </div>
          </motion.div>
        </div>

        <motion.div
          initial={{ opacity: 0, y: 50 }}
          animate={inView ? { opacity: 1, y: 0 } : {}}
          transition={{ duration: 0.8, delay: 0.6 }}
          className="mt-20"
        >
          <h3 className="text-3xl font-bold text-center text-gray-900 mb-12">
            Değerlerimiz
          </h3>

          <div className="grid md:grid-cols-2 lg:grid-cols-4 gap-8">
            {values.map((value, index) => (
              <motion.div
                key={index}
                initial={{ opacity: 0, y: 30 }}
                animate={inView ? { opacity: 1, y: 0 } : {}}
                transition={{ duration: 0.6, delay: 0.8 + index * 0.1 }}
                whileHover={{ y: -5 }}
                className="bg-white p-6 rounded-xl shadow-lg hover:shadow-xl transition-all duration-300 text-center"
              >
                <div className="text-blue-600 mb-4 flex justify-center">
                  {value.icon}
                </div>
                <h4 className="text-xl font-semibold text-gray-900 mb-3">
                  {value.title}
                </h4>
                <p className="text-gray-600">
                  {value.description}
                </p>
              </motion.div>
            ))}
          </div>
        </motion.div>

        <motion.div
          initial={{ opacity: 0, y: 50 }}
          animate={inView ? { opacity: 1, y: 0 } : {}}
          transition={{ duration: 0.8, delay: 1.2 }}
          className="mt-20 bg-white rounded-2xl shadow-xl p-8 md:p-12"
        >
          <div className="grid lg:grid-cols-3 gap-8">
            <div className="text-center">
              <div className="text-4xl font-bold text-blue-600 mb-2">25+</div>
              <div className="text-gray-600 font-medium">Uzman Ekip Üyesi</div>
              <p className="text-sm text-gray-500 mt-2">
                Mimarlar, mühendisler ve uzman ustalar
              </p>
            </div>
            <div className="text-center">
              <div className="text-4xl font-bold text-blue-600 mb-2">10+</div>
              <div className="text-gray-600 font-medium">Sektör Ödülü</div>
              <p className="text-sm text-gray-500 mt-2">
                Mükemmellik ve inovasyon için tanınma
              </p>
            </div>
            <div className="text-center">
              <div className="text-4xl font-bold text-blue-600 mb-2">%100</div>
              <div className="text-gray-600 font-medium">Müşteri Memnuniyeti</div>
              <p className="text-sm text-gray-500 mt-2">
                Beklentileri aşmaya kararlı
              </p>
            </div>
          </div>
        </motion.div>
      </div>
    </section>
  );
};

export default About;
