import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { useInView } from 'react-intersection-observer';
import { FaPhone, FaEnvelope, FaMapMarkerAlt, FaClock, FaBuilding } from 'react-icons/fa';

const Contact = () => {
  const [ref, inView] = useInView({
    threshold: 0.1,
    triggerOnce: true,
  });

  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phone: '',
    projectType: '',
    message: ''
  });

  const [isSubmitting, setIsSubmitting] = useState(false);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsSubmitting(true);

    // Simulate form submission
    await new Promise(resolve => setTimeout(resolve, 2000));

    // Reset form
    setFormData({
      name: '',
      email: '',
      phone: '',
      projectType: '',
      message: ''
    });

    setIsSubmitting(false);
    alert('Teşekkürler! Mesajınız başarıyla gönderildi.');
  };

  const contactInfo = [
    {
      icon: <FaPhone className="text-2xl" />,
      title: "Telefon",
      details: ["+90 (212) 123-4567", "+90 (212) 987-6543"],
      color: "bg-blue-500"
    },
    {
      icon: <FaEnvelope className="text-2xl" />,
      title: "E-posta",
      details: ["info@sitee.com", "projeler@sitee.com"],
      color: "bg-green-500"
    },
    {
      icon: <FaMapMarkerAlt className="text-2xl" />,
      title: "Adres",
      details: ["İnşaat Caddesi No:123", "İş Merkezi", "İstanbul, Türkiye"],
      color: "bg-red-500"
    },
    {
      icon: <FaClock className="text-2xl" />,
      title: "Çalışma Saatleri",
      details: ["Pzt-Cuma: 08:00 - 18:00", "Cmt: 09:00 - 15:00"],
