import React from 'react';
import { motion } from 'framer-motion';
import { useInView } from 'react-intersection-observer';
import { FaAward, FaUsers, FaHandshake, FaLightbulb } from 'react-icons/fa';

const About = () => {
  const [ref, inView] = useInView({
    threshold: 0.1,
    triggerOnce: true,
  });

  const values = [
    {
      icon: <FaAward className="text-3xl" />,
      title: "Excellence",
      description: "We strive for excellence in every project, delivering the highest quality standards."
    },
    {
      icon: <FaUsers className="text-3xl" />,
      title: "Collaboration",
      description: "Working closely with clients to bring their vision to life with innovative solutions."
    },
    {
      icon: <FaHandshake className="text-3xl" />,
      title: "Integrity",
      description: "Building trust through transparent communication and honest business practices."
    },
    {
      icon: <FaLightbulb className="text-3xl" />,
      title: "Innovation",
      description: "Embracing cutting-edge technology and sustainable practices in all our projects."
    }
  ];

  return (
    <section id="about" className="section-padding bg-gray-50">
      <div className="container-custom">
        <motion.div
          ref={ref}
          initial={{ opacity: 0, y: 50 }}
          animate={inView ? { opacity: 1, y: 0 } : {}}
          transition={{ duration: 0.8 }}
          className="text-center mb-16"
        >
          <h2 className="text-4xl md:text-5xl font-display font-bold text-gray-900 mb-6">
            About <span className="text-gradient">Sitee</span>
          </h2>
          <p className="text-xl text-gray-600 max-w-3xl mx-auto">
            For over two decades, we've been at the forefront of architectural innovation, 
            creating spaces that inspire and endure.
          </p>
        </motion.div>

        <div className="grid lg:grid-cols-2 gap-12 items-center mb-16">
          <motion.div
            initial={{ opacity: 0, x: -50 }}
            animate={inView ? { opacity: 1, x: 0 } : {}}
            transition={{ duration: 0.8, delay: 0.2 }}
            className="relative"
          >
            <div className="relative">
              <div className="w-full h-96 md:h-[500px] bg-gradient-to-br from-primary-600 to-primary-800 rounded-2xl shadow-2xl">
                <div className="absolute inset-0 bg-black/20 rounded-2xl"></div>
                <div className="absolute inset-0 flex items-center justify-center text-white">
                  <div className="text-center">
                    <div className="text-6xl mb-4">🏢</div>
                    <p className="text-xl font-semibold">Modern Office Complex</p>
                    <p className="text-sm opacity-80">Completed 2023</p>
                  </div>
                </div>
              </div>

              <motion.div
                animate={{
                  y: [0, -10, 0],
                }}
                transition={{
                  duration: 4,
                  repeat: Infinity,
                  ease: "easeInOut"
                }}
                className="absolute -top-6 -right-6 bg-white p-4 rounded-lg shadow-lg"
              >
                <div className="text-2xl font-bold text-primary-600">25+</div>
                <div className="text-sm text-gray-600">Years</div>
              </motion.div>
            </div>
          </motion.div>

          <motion.div
            initial={{ opacity: 0, x: 50 }}
            animate={inView ? { opacity: 1, x: 0 } : {}}
            transition={{ duration: 0.8, delay: 0.4 }}
            className="space-y-6"
          >
            <h3 className="text-3xl font-display font-bold text-gray-900 mb-6">
              Building Dreams Into Reality
            </h3>

            <p className="text-lg text-gray-600 leading-relaxed">
              Founded in 1999, Sitee Construction has grown from a small local contractor 
              to one of the region's most trusted names in construction and architecture. 
              Our journey has been marked by innovation, quality, and an unwavering 
              commitment to our clients' success.
            </p>

            <p className="text-lg text-gray-600 leading-relaxed">
              We specialize in commercial, residential, and industrial construction, 
              offering comprehensive services from initial design to final completion. 
              Our team of experienced professionals brings together expertise in 
              architecture, engineering, and project management.
            </p>

            <div className="grid grid-cols-2 gap-6 pt-6">
              <div className="text-center">
                <div className="text-3xl font-bold text-primary-600 mb-2">500+</div>
                <div className="text-gray-600">Projects Completed</div>
              </div>
              <div className="text-center">
                <div className="text-3xl font-bold text-primary-600 mb-2">150+</div>
                <div className="text-gray-600">Happy Clients</div>
              </div>
            </div>
          </motion.div>
        </div>

        <motion.div
          initial={{ opacity: 0, y: 50 }}
          animate={inView ? { opacity: 1, y: 0 } : {}}
          transition={{ duration: 0.8, delay: 0.6 }}
          className="mt-20"
        >
          <h3 className="text-3xl font-display font-bold text-center text-gray-900 mb-12">
            Our Core Values
          </h3>

          <div className="grid md:grid-cols-2 lg:grid-cols-4 gap-8">
            {values.map((value, index) => (
              <motion.div
                key={index}
                initial={{ opacity: 0, y: 30 }}
                animate={inView ? { opacity: 1, y: 0 } : {}}
                transition={{ duration: 0.6, delay: 0.8 + index * 0.1 }}
                whileHover={{ y: -5 }}
                className="bg-white p-6 rounded-xl shadow-lg hover:shadow-xl transition-all duration-300 text-center"
              >
                <div className="text-primary-600 mb-4 flex justify-center">
                  {value.icon}
                </div>
                <h4 className="text-xl font-semibold text-gray-900 mb-3">
                  {value.title}
                </h4>
                <p className="text-gray-600">
                  {value.description}
                </p>
              </motion.div>
            ))}
          </div>
        </motion.div>

        <motion.div
          initial={{ opacity: 0, y: 50 }}
          animate={inView ? { opacity: 1, y: 0 } : {}}
          transition={{ duration: 0.8, delay: 1.2 }}
          className="mt-20 bg-white rounded-2xl shadow-xl p-8 md:p-12"
        >
          <div className="grid lg:grid-cols-3 gap-8">
            <div className="text-center">
              <div className="text-4xl font-bold text-primary-600 mb-2">50+</div>
              <div className="text-gray-600 font-medium">Expert Team Members</div>
              <p className="text-sm text-gray-500 mt-2">
                Architects, engineers, and skilled craftsmen
              </p>
            </div>
            <div className="text-center">
              <div className="text-4xl font-bold text-primary-600 mb-2">15+</div>
              <div className="text-gray-600 font-medium">Industry Awards</div>
              <p className="text-sm text-gray-500 mt-2">
                Recognition for excellence and innovation
              </p>
            </div>
            <div className="text-center">
              <div className="text-4xl font-bold text-primary-600 mb-2">100%</div>
              <div className="text-gray-600 font-medium">Client Satisfaction</div>
              <p className="text-sm text-gray-500 mt-2">
                Committed to exceeding expectations
              </p>
            </div>
          </div>
        </motion.div>
      </div>
    </section>
  );
};

export default About;
