import React from 'react';
import { motion } from 'framer-motion';
import { useInView } from 'react-intersection-observer';
import { FaBuilding, FaHome, FaIndustry, FaPencilRuler, FaCogs, FaLeaf } from 'react-icons/fa';

const Services = () => {
  const [ref, inView] = useInView({
    threshold: 0.1,
    triggerOnce: true,
  });

  const services = [
    {
      icon: <FaBuilding className="text-4xl" />,
      title: "Ticari İnşaat",
      description: "Modern ofis binaları, alışveriş merkezleri ve ticari kompleksler.",
      features: ["Ofis Binaları", "Alışveriş Merkezleri", "Otel ve Tesisler", "Restoranlar"]
    },
    {
      icon: <FaHome className="text-4xl" />,
      title: "Konut Projeleri",
      description: "Lüks evler ve konut toplulukları ile konfor ve sofistike tasarım.",
      features: ["Özel Evler", "Apartman Kompleksleri", "Müstakil Evler", "Lüks Villalar"]
    },
    {
      icon: <FaIndustry className="text-4xl" />,
      title: "Endüstriyel İnşaat",
      description: "Verimlilik ve dayanıklılık için inşa edilmiş sağlam endüstriyel tesisler.",
      features: ["Üretim Tesisleri", "Depolar", "Dağıtım Merkezleri", "Endüstriyel Parklar"]
    },
    {
      icon: <FaPencilRuler className="text-4xl" />,
      title: "Mimari Tasarım",
      description: "Estetik ve işlevselliği birleştiren yenilikçi mimari çözümler.",
      features: ["Konsept Tasarım", "3D Modelleme", "İç Tasarım", "Peyzaj Tasarımı"]
    },
    {
      icon: <FaCogs className="text-4xl" />,
      title: "Proje Yönetimi",
      description: "Zamanında teslim ve kalite kontrol sağlayan kapsamlı proje yönetimi.",
      features: ["Planlama ve Programlama", "Maliyet Kontrolü", "Kalite Güvencesi", "Risk Yönetimi"]
    },
    {
      icon: <FaLeaf className="text-4xl" />,
      title: "Sürdürülebilir Yapı",
      description: "Çevresel sorumluluk ve enerji verimliliğini öncelik edinen yeşil bina çözümleri.",
      features: ["LEED Sertifikası", "Enerji Verimliliği", "Yeşil Malzemeler", "Güneş Entegrasyonu"]
    }
  ];

  return (
    <section id="services" className="section-padding bg-white">
      <div className="container-custom">
        <motion.div
          ref={ref}
          initial={{ opacity: 0, y: 50 }}
          animate={inView ? { opacity: 1, y: 0 } : {}}
          transition={{ duration: 0.8 }}
          className="text-center mb-16"
        >
          <h2 className="text-4xl md:text-5xl font-bold text-gray-900 mb-6">
            Hizmetlerimiz
          </h2>
          <p className="text-xl text-gray-600 max-w-3xl mx-auto">
            Vizyonunuzu karşılayan ve beklentilerinizi aşan kapsamlı inşaat ve mimari hizmetler.
          </p>
        </motion.div>

        <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-8">
          {services.map((service, index) => (
            <motion.div
              key={index}
              initial={{ opacity: 0, y: 30 }}
              animate={inView ? { opacity: 1, y: 0 } : {}}
              transition={{ duration: 0.6, delay: index * 0.1 }}
              whileHover={{ y: -5 }}
              className="bg-white rounded-2xl shadow-lg hover:shadow-xl transition-all duration-300 p-8 border border-gray-100"
            >
              <div className="text-blue-600 mb-6 flex justify-center">
                {service.icon}
              </div>

              <h3 className="text-2xl font-bold text-gray-900 mb-4 text-center">
                {service.title}
              </h3>

              <p className="text-gray-600 mb-6 text-center leading-relaxed">
                {service.description}
              </p>

              <ul className="space-y-2">
                {service.features.map((feature, featureIndex) => (
                  <li key={featureIndex} className="flex items-center text-sm text-gray-600">
                    <div className="w-2 h-2 bg-blue-600 rounded-full mr-3"></div>
                    {feature}
                  </li>
                ))}
              </ul>
            </motion.div>
          ))}
        </div>

        {/* Process Section */}
        <motion.div
          initial={{ opacity: 0, y: 50 }}
          animate={inView ? { opacity: 1, y: 0 } : {}}
          transition={{ duration: 0.8, delay: 0.8 }}
          className="mt-20 bg-gradient-to-r from-blue-600 to-blue-800 rounded-3xl p-8 md:p-12 text-white"
        >
          <div className="text-center mb-12">
            <h3 className="text-3xl md:text-4xl font-bold mb-6">
              Çalışma Sürecimiz
            </h3>
            <p className="text-xl opacity-90 max-w-3xl mx-auto">
              Olağanüstü sonuçlar sunmak için sistematik yaklaşım
            </p>
          </div>

          <div className="grid md:grid-cols-4 gap-8">
            {[
              {
                step: "01",
                title: "Görüşme",
                description: "Vizyonunuzu ve gereksinimlerinizi anlama"
              },
              {
                step: "02",
                title: "Tasarım",
                description: "Detaylı planlar ve 3D modeller oluşturma"
              },
              {
                step: "03",
                title: "İnşaat",
                description: "Projeyi hassasiyetle yürütme"
              },
              {
                step: "04",
                title: "Teslim",
                description: "Hayalinizdeki projeyi zamanında teslim etme"
              }
            ].map((process, index) => (
              <motion.div
                key={index}
                initial={{ opacity: 0, scale: 0.8 }}
                animate={inView ? { opacity: 1, scale: 1 } : {}}
                transition={{ duration: 0.6, delay: 1 + index * 0.1 }}
                className="text-center"
              >
                <div className="w-16 h-16 bg-white/20 rounded-full flex items-center justify-center mx-auto mb-4">
                  <span className="text-2xl font-bold">{process.step}</span>
                </div>
                <h4 className="text-xl font-semibold mb-2">{process.title}</h4>
                <p className="text-sm opacity-80">{process.description}</p>
              </motion.div>
            ))}
          </div>
        </motion.div>

        {/* Call to Action */}
        <motion.div
          initial={{ opacity: 0, y: 50 }}
          animate={inView ? { opacity: 1, y: 0 } : {}}
          transition={{ duration: 0.8, delay: 1.4 }}
          className="mt-16 text-center"
        >
          <div className="bg-gray-50 rounded-2xl p-8 md:p-12">
            <h3 className="text-3xl font-bold text-gray-900 mb-4">
              Projenizi Başlatmaya Hazır mısınız?
            </h3>
            <p className="text-lg text-gray-600 mb-8 max-w-2xl mx-auto">
              Vizyonunuzu tartışalım ve birlikte olağanüstü bir şey yaratalım. 
              Ekibimiz fikirlerinizi hayata geçirmeye hazır.
            </p>
            <motion.button
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
              onClick={() => {
                const element = document.querySelector('#contact');
                if (element) {
                  element.scrollIntoView({ behavior: 'smooth' });
                }
              }}
              className="bg-blue-600 hover:bg-blue-700 text-white font-semibold py-4 px-8 rounded-lg transition-all duration-300 text-lg"
            >
              Ücretsiz Danışmanlık Al
            </motion.button>
          </div>
        </motion.div>
      </div>
    </section>
  );
};

export default Services;
